function coeffs_masked=coeff_mask(coeffs,spara,N)% Sparar de spara % stšrsta koefficienterna i varje NxN-block. Nollar resten.k=round(spara/100*N*N);i=1;j=1;[a,b]=size(coeffs);while (i<a) & (j<b)	f=coeffs(i:i+N-1,j:j+N-1);	f2=reshape(f,N^2,1);	[Y,I]=sort(abs(f2));	f2(I(1:N^2-k))=0;	coeffs_masked(i:i+N-1,j:j+N-1)=reshape(f2,N,N);	i=i+N;	if i>a		i=1;		j=j+N;	end;end;