function DCTMat=dct_basis(N)% Beräknar DCT basfunktionerna (2-d) för blockstorlek Nfigurealpha=[sqrt(1/N) sqrt(2/N)*ones(1,N-1)];for u=0:N-1; 	for v=0:N-1;  		for x=1:N;   			for y=1:N;    			x1=x-1;y1=y-1;				    			DCTMat(u+1,v+1,x,y)=alpha(u+1)*alpha(v+1)*cos((2*x1+1)*u*pi/(2*N))*cos((2*y1+1)*v*pi/(2*N));			end;  		end;  		  		subplot(N,N,u*N+v+1);		A=reshape(DCTMat(u+1,v+1,:,:),N,N);  		imagesc(A);colormap('gray');  		ylabel('x');xlabel('y'); 	end;end;