function DFTMat=dft_basis(N)for u=0:N-1; 	for v=0:N-1;  		for x=1:N;   			for y=1:N;    			x1=x-1;y1=y-1;    			DFTMat(u+1,v+1,x,y)=1/N*exp(-2i*pi/N)^(u*x1+v*y1);   			end;  		end;		A=reshape(DFTMat(u+1,v+1,:,:),N,N);		figure(1)  		subplot(N,N,u*N+v+1);  		imagesc(real(A));colormap('gray');  		ylabel('x');xlabel('y');		figure(2)  		subplot(N,N,u*N+v+1);  		imagesc(imag(A));colormap('gray');  		ylabel('x');xlabel('y'); 	end;end;