function coeffs=pic_coeff(bild,TransMat,N)% BerŠknar transformkoefficienterna fšr bilden bild med hjŠlp % av basfunktionerna i TransMat och blockstorleken Ni=1;j=1;bild=double(bild);[a,b]=size(bild);while (i<a) & (j<b)	f=bild(i:i+N-1,j:j+N-1);	for u=0:N-1		for v=0:N-1			A=reshape(TransMat(u+1,v+1,:,:),N,N);			C(i+u,j+v)=sum(sum(A.*f));		end;	end;	i=i+N;	if i>a		i=1;		j=j+N;	end;end;coeffs=C;	