function bild_rec=pic_reconstr(coeffs,TransMat,N);% Rekonstruerar bilden från transformkoefficienterna% med hjälp av basfunktionerna i TransMat och blockstorleken Ni=1;j=1;[a,b]=size(coeffs);while (i<a) & (j<b)	f=zeros(N);	for u=0:N-1		for v=0:N-1			A=reshape(TransMat(u+1,v+1,:,:),N,N);			f=f+coeffs(i+u,j+v)*conj(A);		end;	end;	bild_rec(i:i+N-1,j:j+N-1)=f;	i=i+N;	if i>a		i=1;		j=j+N;	end;end;